import { readFileSync } from "fs";
import { join } from "path";

import parseJBEI from "./jbei";

describe("JBEI parser", () => {
  it("should parse a JBEI file", () => {
    const file = readFileSync(join(__dirname, "..", "examples", "jbei", "pBbE0c-RFP.seq"), "utf8");

    const seqs = parseJBEI(file);

    expect(seqs).toEqual([
      {
        annotations: [
          {
            direction: -1,
            end: 2670,
            name: "CmR",
            start: 2010,
            type: "gene",
          },
          {
            direction: 1,
            end: 915,
            name: "RFP cassette",
            start: 2811,
            type: "gene",
          },
          {
            direction: -1,
            end: 1884,
            name: "colE1 origin",
            start: 1201,
            type: "rep_origin",
          },
          {
            direction: 1,
            end: 1995,
            name: "T0",
            start: 1889,
            type: "terminator",
          },
        ],
        name: "pBbE0c-RFP",
        seq: "",
        type: "dna",
      },
    ]);
  });
});
